import {Text, View, StyleSheet, ScrollView} from 'react-native'
import CardShowBecas from './components/Card/CardShowBeca';
import BecasNacionales from './components/Nacionales/BecasNacionales';
import Headernav from './components/navbar/navbar';
import Abstract from './components/Noticias/Abstract';


export default function App() {

  return (
    <ScrollView>
      <View style ={styles.container}>
          <Headernav/>
          <View className='main-content'>
            <View className='Most-popular'>
              <BecasNacionales/>
            </View>
            <View style={styles.seperator}/>
            <View className='News'>
              <Text style = {styles.textStyle}>Noticias</Text>
              <Abstract/>
            </View>  
          </View>
      </View>
    </ScrollView>
  );
}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#1E2029',
  },
  textStyle:{
    color:"white",
    fontSize: 20,
  },
  seperator: {
    width: 300,
    height: 10,
    backgroundColor: 'red',
  },
})
