import React from "react";
import {useState, useEffect} from 'react';
import {getBecas} from "../../Funciones/BackBecas.js";
import './verDetalles.css';



function VerDetalles(){

    const params = useParams();

    const initialSatate = {id:0, nombre: "", porcentaje: 0, pais: "", universidad: "", requerimientos: ""};

    const[BecaNueva, setBecaNueva] = useState(initialSatate);

    const handleInputChange = (e) => {
        
        setBecaNueva({ ...BecaNueva, [e.target.name]: e.target.value});
    };

    const getBeca = async (becaId) =>{
        try{
            const res=await getBecas(becaId);
            const data= await res.json();
            const { nombre, porcentaje, pais, universidad, requerimientos} = data.becas[0];
            setBecaNueva({ nombre, porcentaje, pais, universidad, requerimientos});
        }catch(error){
            console.log(error);
        }
    };

    useEffect(() =>{
        if(params.id){
          getBeca(params.id);  
        }
        // eslint-disable-next-line
    }, []);

    return(
        <View>
            <Headernav/>
            <Text style={styles.textStyle}>
                Detalles Becas
            </Text>
            <TextInput
                label="Nombre"
                value={nombre}
                mode = "outlined"

                onChangeText={text => setNombre(text)}
            />
            <TextInput
                label="Porcentaje"
                value={porcentaje}
                mode = "outlined"

                onChangeText={text => setPorcentaje(text)}
            />
            <TextInput
                label="Pais"
                value={pais}
                mode = "outlined"

                onChangeText={text => setPais(text)}
            />
            <TextInput
                label="Universidad"
                value={universidad}
                mode = "outlined"
                onChangeText={text => setUniversidad(text)}
            />
            <TextInput
                label="Requerimientos"
                value={requerimientos}
                mode = "outlined"
                onChangeText={text => setRequerimientos(text)}
            />

        </View>
    );
}

export default VerDetalles;
