import React from "react";
import { ScrollView, View, StyleSheet, Text } from "react-native";
import { Card, Title, Paragraph, Button } from "react-native-paper";
import Grado from "../../Assets/img/Grado.jpg"
import { NavigationContainer, useNavigation } from '@react-navigation/native';

function CardShowBecas({beca,props}){

    const navigation = useNavigation();

    const handleDelete = async (becaId)=>{
      await DelteBecas(becaId);
      if(beca.pais === "Colombia" || beca.pais === "colombia"){
        navigation.navigate('BecasNacionales');  
      }else{
        navigation.navigate("BecasInternacionales");  
      }
    };

    

    return( 
          <Card style={styles.carder}>
            <Card.Content>
              <Title style={styles.fontColor}>{beca.nombre}</Title>
              <Card.Cover source={{ uri:"../../Assets/img/Grado.jpg"}} />
              <Paragraph style={styles.fontColor}>Universidad:{beca.universidad}</Paragraph>
              <Paragraph style={styles.fontColor}>Pais:{beca.pais}</Paragraph>
              <Paragraph style={styles.fontColor}>Porcentaje{beca.porcentaje}</Paragraph>
              <Paragraph style={styles.fontColor}>Requerimientos:{beca.requerimientos}</Paragraph>
            </Card.Content>
            <Card.Actions>
              <Button><Text style={styles.fontColor}>Detalles</Text></Button>
              <Button><Text style={styles.fontColor}>Actualizar</Text></Button>
              <Button onPress={() => handleDelete()}><Text style={styles.fontColor}>Eliminar</Text></Button>
            </Card.Actions>
          </Card>
    );
}

const styles = StyleSheet.create({

  seperator: {
    width: 600,
    height: 50,
    backgroundColor: 'black',
  },
  carder:{
      margin:10,
      padding:10,
      backgroundColor:'#6804ec',

  },
  fontColor:{
      color:"white",
  }
})

export default CardShowBecas;
