import React from "react";
import { Card } from "react-native-paper";


function NewsCard({news}){

    return(
        <Card>
            <Title title="Card Title" subtitle="Card Subtitle"/>
            <Card.Content>
                <Title>{news.Title}</Title>
                <Paragraph>{news.abstract}</Paragraph>
            </Card.Content>
            <Card.Cover source={ news.multimedia[1].url } />
            <Card.Actions>
                <Button>Cancel</Button>
                <Button>Ok</Button>
            </Card.Actions>
        </Card>
    );
}

export default NewsCard;
