import React from "react";
import {useState, useEffect} from 'react';
import { useParams } from "react-router-native";
import {registerBecas, getBecas, updateBecas} from "../../Funciones/BackBecas.js";
import { ScrollView, View, Text, StyleSheet, Alert } from "react-native";
import { Card, Title, Paragraph, Button, TextInput } from "react-native-paper";
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import Headernav from "../navbar/navbar.js";




function VerDetalles({route}){

    const BecaNueva = route.params;

    console.log(BecaNueva)


    
    return(
        <View>
            <Headernav/>
            <Text style={styles.textStyle}>
                Detalles Becas
            </Text>
            <TextInput
                label="Nombre"
                value={BecaNueva.nombre}
                mode = "outlined"

            />
            <TextInput
                label="Porcentaje"
                value={BecaNueva.porcentaje}
                mode = "outlined"

            
            />
            <TextInput
                label="Pais"
                value={BecaNueva.pais}
                mode = "outlined"


            />
            <TextInput
                label="Universidad"
                value={BecaNueva.universidad}
                mode = "outlined"
   
            />
            <TextInput
                label="Requerimientos"
                value={BecaNueva.requerimientos}
                mode = "outlined"
                
            />

        </View>
    );
}

const styles = StyleSheet.create({
    textStyle:{
        color:"white",
        fontSize: 20,
      },
      carder:{
          margin:10,
          padding:10,
          backgroundColor:'#6804ec',
  
      },
  
    })

export default VerDetalles;
