import React from "react";
import {useState, useEffect} from 'react';
import { useParams } from "react-router-native";
import {registerBecas, getBecas, updateBecas} from "../../Funciones/BackBecas.js";
import { ScrollView, View, Text, StyleSheet, Alert } from "react-native";
import { Card, Title, Paragraph, Button, TextInput } from "react-native-paper";
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import Headernav from "../navbar/navbar.js";




function VerDetalles({beca}){

    const becas = route.params;

    const initialSatate = {id:0, nombre: "", porcentaje: 0, pais: "", universidad: "", requerimientos: ""};

    const[BecaNueva, setBecaNueva] = useState(initialSatate);

    const handleInputChange = (e) => {
        
        setBecaNueva({ ...BecaNueva, [e.target.name]: e.target.value});
    };

    const getBeca = async (becaId) =>{
        try{
            const res=await getBecas(becaId);
            const data= await res.json();
            const { nombre, porcentaje, pais, universidad, requerimientos} = data.becas[0];
            setBecaNueva({ nombre, porcentaje, pais, universidad, requerimientos});
        }catch(error){
            console.log(error);
        }
    };

    return(
        <View>
            <Headernav/>
            <Text style={styles.textStyle}>
                Detalles Becas
            </Text>
            <TextInput
                label="Nombre"
                value={beca.nombre}
                mode = "outlined"

            />
            <TextInput
                label="Porcentaje"
                value={beca.porcentaje.toString()}
                mode = "outlined"

            
            />
            <TextInput
                label="Pais"
                value={beca.pais}
                mode = "outlined"


            />
            <TextInput
                label="Universidad"
                value={beca.universidad}
                mode = "outlined"
   
            />
            <TextInput
                label="Requerimientos"
                value={beca.requerimientos}
                mode = "outlined"
                
            />

        </View>
    );
}

const styles = StyleSheet.create({
    textStyle:{
        color:"white",
        fontSize: 20,
      },
      carder:{
          margin:10,
          padding:10,
          backgroundColor:'#6804ec',
  
      },
  
    })

export default VerDetalles;
